import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import NavBar from './components/NavBar';
import News from './components/News';
import All from './components/All'; // Import the All component
import LoadingBar from 'react-top-loading-bar';

export default class App extends Component {
  state = {
    progress: 0,
    country: 'in', // Default country is set to IN
  };

  componentDidMount() {
    this.increaseProgress();
  }

  componentWillUnmount() {
    clearTimeout(this.progressTimer);
  }

  increaseProgress = () => {
    const interval = 10;
    const targetProgress = 100;
    const step = 1;
    let currentProgress = 0;

    this.progressTimer = setInterval(() => {
      currentProgress += step;
      if (currentProgress >= targetProgress) {
        clearInterval(this.progressTimer);
      }
      this.setState({ progress: currentProgress });
    }, interval);
  };

  handleCountryChange = (country) => {
    this.setState({ country });
  };

  pageSize = 5;
  // apiKey = process.env.REACT_APP_NEWS_API;
  apiKey = "51818d35daed4d1e823badfdba2ebd50";

  render() {
    return (
      <div>
        <Router>
          <NavBar onChangeCountry={this.handleCountryChange} />
          <LoadingBar color='#f11946' height={3} progress={this.state.progress} />
          <Switch>
            {/* default page open i can use redirect to  */}
            <Redirect exact from="/" to="/all" />
            {/* ---------------------------------------- */}
            <Route path='/all'>
              <All /> {/* Render the All component */}
            </Route>
            <Route path='/:category'>
              <News pageSize={this.pageSize} apiKey={this.apiKey} country={this.state.country} />
            </Route>
          </Switch>
        </Router>
      </div>
    );
  }
}
