import React, { Component } from 'react';

export default class All extends Component {
  render() {
    return (
      <>
        <div className="container-fluid bg-primary-subtle text-primary-emphasis text-light py-5">
          <div className="row justify-content-center">
            <div className="col-md-8 text-center">
              <h1 className="display-4 mb-4">Welcome to KTNews!</h1>
              <p className="lead mb-5">Stay informed with the latest news across various categories.</p>
            </div>
          </div>
        </div>

        <div className="container py-5">
          <div className="row justify-content-center">

            <div className="col-md-4 mb-4">
              <div className="card text-center border-0 shadow">
                <div className="card-body">
                  <h5 className="card-title">Business</h5>
                  <p className="card-text">Get the latest updates on business news.</p>
                  <a href="/business" className="btn btn-primary rounded-pill">Business</a>
                </div>
              </div>
            </div>
            {/* Repeat similar card structures for other categories */}
            {/* Example: */}
            <div className="col-md-4 mb-4">
              <div className="card text-center border-0 shadow">
                <div className="card-body">
                  <h5 className="card-title">Entertainment</h5>
                  <p className="card-text">Stay entertained with the latest entertainment news.</p>
                  <a href="/entertainment" className="btn btn-primary rounded-pill">Explore</a>
                </div>
              </div>
            </div>

          </div>
        </div>
      </>
    );
  }
}
